package week1.day1;

public class OddOrEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 21;
		
		if(a%2==0)
		{
			System.out.println("The given number " + a+ " is Even");
		}
		else
		{
			System.out.println("The given number " + a+ " is Odd");

		}
	}

}
